<template>
  <div class="content" :class="bgColor">
    <div class="flex flex-col items-center justify-center h-screen">
      <h1 class="text-[40vw] font-bold leading-none">{{ countdown }}</h1>
      <h3 class="text-3xl mt-4">{{ shootingGroup }} Shooting</h3>
      <p class="absolute top-8 left-8 text-xl">{{ currentTime }}</p>
      <button type="button" @click="startCountdown(120)" class="mt-8 text-2xl px-8 py-4 bg-blue-500 text-white rounded-lg hover:bg-blue-600">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" class="size-6">
          <path fill-rule="evenodd" d="M4.5 5.653c0-1.427 1.529-2.33 2.779-1.643l11.54 6.347c1.295.712 1.295 2.573 0 3.286L7.28 19.99c-1.25.687-2.779-.217-2.779-1.643V5.653Z" clip-rule="evenodd" />
        </svg>
      </button>
      <!-- <button type="button" @click="timer ? clearInterval(timer) : startCountdown(countdown)" class="mt-4 text-2xl px-8 py-4 bg-blue-500 text-white rounded-lg hover:bg-blue-600">
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" class="size-6">
          <path fill-rule="evenodd" d="M6.75 5.25a.75.75 0 0 1 .75-.75H9a.75.75 0 0 1 .75.75v13.5a.75.75 0 0 1-.75.75H7.5a.75.75 0 0 1-.75-.75V5.25zm7.5 0A.75.75 0 0 1 15 4.5h1.5a.75.75 0 0 1 .75.75v13.5a.75.75 0 0 1-.75.75H15a.75.75 0 0 1-.75-.75V5.25z" clip-rule="evenodd" />
        </svg>
      </button> -->
    </div>
    <div class="absolute right-8 top-8">
      <button type="button" @click="showSettings = !showSettings" class="size-6">
      <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor">
        <path d="M18.75 12.75h1.5a.75.75 0 0 0 0-1.5h-1.5a.75.75 0 0 0 0 1.5ZM12 6a.75.75 0 0 1 .75-.75h7.5a.75.75 0 0 1 0 1.5h-7.5A.75.75 0 0 1 12 6ZM12 18a.75.75 0 0 1 .75-.75h7.5a.75.75 0 0 1 0 1.5h-7.5A.75.75 0 0 1 12 18ZM3.75 6.75h1.5a.75.75 0 1 0 0-1.5h-1.5a.75.75 0 0 0 0 1.5ZM5.25 18.75h-1.5a.75.75 0 0 1 0-1.5h1.5a.75.75 0 0 1 0 1.5ZM3 12a.75.75 0 0 1 .75-.75h7.5a.75.75 0 0 1 0 1.5h-7.5A.75.75 0 0 1 3 12ZM9 3.75a2.25 2.25 0 1 0 0 4.5 2.25 2.25 0 0 0 0-4.5ZM12.75 12a2.25 2.25 0 1 1 4.5 0 2.25 2.25 0 0 1-4.5 0ZM9 15.75a2.25 2.25 0 1 0 0 4.5 2.25 2.25 0 0 0 0-4.5Z" />
      </svg>
      </button>
      <div v-if="showSettings" class="absolute right-0 mt-2 bg-white rounded-lg shadow-lg p-4">
      <div class="flex flex-col gap-2">
        <label class="text-black">Countdown (seconds):</label>
        <input type="number" v-model="customCountdown" class="border p-2 rounded text-black" min="1">
        <label class="text-black">Shooting Group:</label>
        <select v-model="shootingGroup" class="border p-2 rounded text-black">
          <option v-for="group in shootingGroups" :key="group" :value="group">{{ group }}</option>
        </select>
        <button @click="() => { startCountdown(customCountdown); showSettings = false; }" class="bg-blue-500 text-white px-4 py-2 rounded">
        Apply
        </button>
      </div>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  data() {
    return {
      currentTime: new Date().toLocaleTimeString(),
      countdown: 120,
      timer: null,
      clockTimer: null,
      bgColor: 'bg-green-500',
      beepSound: new Audio('data:audio/wav;base64,'),
      showSettings: false,
      customCountdown: 120,
      shootingGroups: ["A/B", "C/D"],
      shootingGroup: "A/B",
    }
  },
  methods: {
    startCountdown(seconds) {
      clearInterval(this.timer);
      this.countdown = seconds;
      this.bgColor = 'bg-green-500';
      this.timer = setInterval(() => {
      if (this.countdown > 0) {
        this.countdown--;
        if (this.countdown <= 5) {
        this.beepSound.play();
        }
        if (this.countdown <= 10) {
        this.bgColor = 'bg-red-500';
        } else if (this.countdown <= 30) {
        this.bgColor = 'bg-yellow-500';
        }
      } else {
        clearInterval(this.timer);
        this.beepSound.play();
        setTimeout(() => {
        this.beepSound.pause();
        this.beepSound.currentTime = 0;
        }, 2000);
        
        // Switch shooting groups
        const currentIndex = this.shootingGroups.indexOf(this.shootingGroup);
        this.shootingGroup = this.shootingGroups[(currentIndex + 1) % this.shootingGroups.length];
      }
      }, 1000);
    },
    updateCurrentTime() {
      this.currentTime = new Date().toLocaleTimeString();
    }
  },
  mounted() {
    this.clockTimer = setInterval(this.updateCurrentTime, 1000);
  },
  beforeUnmount() {
    clearInterval(this.timer);
    clearInterval(this.clockTimer);
  }
}
</script>

